                        continue;
                    &#125;else if(ret==0)&#123;
                        printf("client close\n");
                        close(i);
                        FD_CLR(i,&amp;oldset);
                    &#125;else&#123;
                        printf("%s\n",buf);
                        Write(i,buf,ret);
                    &#125;
                &#125;
            &#125;
        &#125;
    &#125;
&#125;

